      out.write("*/\r\n");
      out.write("\r\n");
      out.write("/*\r\n");
      out.write("*   @constructor ButtonExpand\r\n");
      out.write("*\r\n");
      out.write("*\r\n");
      out.write("*/\r\n");
      out.write("var ButtonExpand = function (domNode) {\r\n");
      out.write("\r\n");
      out.write("  this.domNode = domNode;\r\n");
      out.write("\r\n");
      out.write("  this.keyCode = Object.freeze({\r\n");
      out.write("    'RETURN': 13\r\n");
      out.write("  });\r\n");
      out.write("};\r\n");
      out.write("\r\n");
      out.write("ButtonExpand.prototype.init = function () {\r\n");
      out.write("\r\n");
      out.write("  this.controlledNode = false;\r\n");
      out.write("\r\n");
      out.write("  var id = this.domNode.getAttribute('aria-controls');\r\n");
      out.write("\r\n");
      out.write("  if (id) {\r\n");
      out.write("    this.controlledNode = document.getElementById(id);\r\n");
      out.write("  }\r\n");
      out.write("\r\n");
      out.write("  this.domNode.setAttribute('aria-expanded', 'false');\r\n");
      out.write("  this.hideContent();\r\n");
      out.write("\r\n");
      out.write("  this.domNode.addEventListener('keydown',    this.handleKeydown.bind(this));\r\n");
      out.write("  this.domNode.addEventListener('click',      this.handleClick.bind(this));\r\n");
      out.write("  this.domNode.addEventListener('focus',      this.handleFocus.bind(this));\r\n");
      out.write("  this.domNode.addEventListener('blur',       this.handleBlur.bind(this));\r\n");
      out.write("\r\n");
      out.write("};\r\n");
      out.write("\r\n");
      out.write("ButtonExpand.prototype.showContent = function () {\r\n");
      out.write("\r\n");
      out.write("  if (this.controlledNode) {\r\n");
      out.write("    this.controlledNode.style.display = 'block';\r\n");
      out.write("  }\r\n");
      out.write("\r\n");
      out.write("};\r\n");
      out.write("\r\n");
      out.write("ButtonExpand.prototype.hideContent = function () {\r\n");
      out.write("\r\n");
      out.write("  if (this.controlledNode) {\r\n");
      out.write("    this.controlledNode.style.display = 'none';\r\n");
      out.write("  }\r\n");
      out.write("\r\n");
      out.write("};\r\n");
      out.write("\r\n");
      out.write("ButtonExpand.prototype.toggleExpand = function () {\r\n");
      out.write("\r\n");
      out.write("  if (this.domNode.getAttribute('aria-expanded') === 'true') {\r\n");
      out.write("    this.domNode.setAttribute('aria-expanded', 'false');\r\n");
      out.write("    this.hideContent();\r\n");
      out.write("  }\r\n");
      out.write("  else {\r\n");
      out.write("    this.domNode.setAttribute('aria-expanded', 'true');\r\n");
      out.write("    this.showContent();\r\n");
      out.write("  }\r\n");
      out.write("\r\n");
      out.write("};\r\n");
      out.write("\r\n");
      out.write("/* EVENT HANDLERS */\r\n");
      out.write("\r\n");
