      out.write("  switch (event.keyCode) {\r\n");
      out.write("\r\n");
      out.write("    case this.keyCode.RETURN:\r\n");
      out.write("\r\n");
      out.write("      this.toggleExpand();\r\n");
      out.write("\r\n");
      out.write("      event.stopPropagation();\r\n");
      out.write("      event.preventDefault();\r\n");
      out.write("      break;\r\n");
      out.write("\r\n");
      out.write("    default:\r\n");
